/**
 * @desc function for making string from Date object
 * @param {object} date
 * @return {string}
 */
const dateToUrl = (date) => {
	const month = date.getMonth() + 1;
	const day = date.getDate();
	const year = date.getFullYear();
	return `${year}-${month}-${day}`;
};

export default dateToUrl;

/**
 * @desc function for showing currently displayed candidates
 * @param {number} page - current page of candidate's list
 * @param {number} totalCandidates
 * @param {number} tableSize
 * @returns {string} - range of currently displayed candidates
 */

const setCurrentRange = (page, totalCandidates, tableSize) => {
	if (!totalCandidates) return `${0}-${0}`;
	let from;
	let to;
	if (totalCandidates < 10) {
		from = 1;
		to = totalCandidates;
	} else if (page !== 0 && page !== Math.floor(totalCandidates / tableSize)) {
		from = page * tableSize + 1;
		to = page * tableSize + tableSize;
	} else if (page === Math.floor(totalCandidates / tableSize)) {
		from = page * tableSize + 1;
		to = totalCandidates;
	} else {
		from = 1;
		to = tableSize;
	}
	return `${from}-${to}`;
};
export default setCurrentRange;

/**
 * @desc function for email validation
 * @param email
 * @returns {boolean}
 */
const validateEmail = (email) => {
	const regexp = /^([a-z0-9_-]+\.)*[a-z0-9_-]+@[a-z0-9_-]+(\.[a-z0-9_-]+)*\.[a-z]{2,6}$/;
	return regexp.test(email);
};

export default validateEmail;

/**
 * @desc function for calculating candidate's job duration
 * @param jobs
 * @returns {string}
 */
const workDuration = (jobs) => {
	const time = jobs.map((job) => job.period).reduce((acc, per) => acc + per, 0);
	let duration;
	if (time === 0) duration = '';
	if (time === 1) duration = `${time} year`;
	if (time > 1) duration = `${time} years`;
	return duration;
};

export default workDuration;

